package com.example.demo;import org.springframework.boot.SpringApplication;import org.springframework.boot.WebApplicationType;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.autoconfigure.mongo.embedded.EmbeddedMongoAutoConfiguration;@SpringBootApplication@EnableAutoConfiguration(exclude = {EmbeddedMongoAutoConfiguration.class})public class SpringBootReactiveMasterApplication {    public static void main(String[] args) {        SpringApplication application = new SpringApplication(SpringBootReactiveMasterApplication.class);        /*         *  - 기본으로 돌아가는 SpringBoot에서는 Reactive TYPE 을 지원안해주니 셋업해야됨         *  */        application.setWebApplicationType(WebApplicationType.REACTIVE);        application.run();    }}